<?php/* Add this on all pages on top. */
set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/*** Standard includes */require_once 'config/database.php';
require_once 'config/smarty.php';/** * Check for login */require_once 'includes/auth.php';require_once 'class/application.php';$applicationObject = new class_application(); if(isset($_GET['delete_code'])) {		$errorArray				= array();	$errorArray['error']	= '';	$errorArray['result']	= 0;		$formValid				= true;	$success					= NULL;	$code						= trim($_GET['delete_code']);		 	if($errorArray['error']  == '' && $errorArray['result']  == 0 ) {		$data	= array();		$data['application_deleted'] = 1;				$where = $applicationObject->getAdapter()->quoteInto('application_code = ?', $code);		$success	= $applicationObject->update($data, $where);					if(is_numeric($success) && $success > 0) {			$errorArray['error']	= '';			$errorArray['result']	= 1;					} else {			$errorArray['error']	= 'Could not delete, please try again.';			$errorArray['result']	= 0;						}	}		echo json_encode($errorArray);	exit;}/* Setup Pagination. */$applicationData = $applicationObject->getByType('NOMINATION');if($applicationData) $smarty->assign_by_ref('applicationData', $applicationData);/* End Pagination Setup. */$smarty->display('awards/nominationapplication/default.tpl');?>