<?php/* Add this on all pages on top. */set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/*** Standard includes */require_once 'config/database.php';require_once 'config/smarty.php';/*** Check for login */ require_once 'includes/auth.php';/* objects. */require_once 'class/awardsection.php';require_once 'class/year.php';$awardsectionObject	= new class_awardsection();$yearObject 			= new class_year();if (isset($_GET['code']) && trim($_GET['code']) != '') {		$code = trim($_GET['code']);		$awardsectionData = $awardsectionObject->getByCode($code);	if($awardsectionData) {		$smarty->assign('awardsectionData', $awardsectionData);	} else {		header('Location: /awards/section/');		exit;			}}$yearpairs = $yearObject->pairs();if($yearpairs) $smarty->assign('yearpairs', $yearpairs);/* Check posted data. */if(count($_POST) > 0) {	$errorArray		= array();	$data 				= array();	$formValid		= true;	$success			= NULL;		if(isset($_POST['awardsection_name']) && trim($_POST['awardsection_name']) == '') {		$errorArray['awardsection_name'] = 'Section name required';		$formValid = false;			}		if(!$awardsectionData) {		if(isset($_POST['year_code']) && trim($_POST['year_code']) == '') {			$errorArray['year_code'] = 'Year of the awards required';			$formValid = false;				}	}		if(count($errorArray) == 0 && $formValid == true) {		$data 	= array();							$data['awardsection_name']	= trim($_POST['awardsection_name']);										if(isset($awardsectionData)) {			/*Update. */			$where		= $awardsectionObject->getAdapter()->quoteInto('awardsection_code = ?', $awardsectionData['awardsection_code']);			$success	= $awardsectionObject->update($data, $where);		} else {						$data['year_code']	= trim($_POST['year_code']);							$success	= $awardsectionObject->insert($data);					}				if(count($errorArray) == 0) {			header('Location: /awards/section/');				exit;				}	}		/* if we are here there are errors. */	$smarty->assign('errorArray', $errorArray);	}$smarty->display('awards/section/details.tpl');?>