<?php/* Add this on all pages on top. */set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/*** Standard includes */require_once 'config/database.php';require_once 'config/smarty.php';/*** Check for login */ require_once 'includes/auth.php';/* objects. */require_once 'class/category.php';require_once 'class/year.php';$categoryObject	= new class_category();$yearObject 			= new class_year();if (isset($_GET['code']) && trim($_GET['code']) != '') {		$code = trim($_GET['code']);		$categoryData = $categoryObject->getByCode($code);	if($categoryData) {		$smarty->assign('categoryData', $categoryData);	} else {		header('Location: /resources/category/');		exit;			}}$yearpairs = $yearObject->pairs();if($yearpairs) $smarty->assign('yearpairs', $yearpairs);/* Check posted data. */if(count($_POST) > 0) {	$errorArray		= array();	$data 				= array();	$formValid		= true;	$success			= NULL;		if(isset($_POST['category_name']) && trim($_POST['category_name']) == '') {		$errorArray['category_name'] = 'Category name required';		$formValid = false;			}		if(isset($_POST['year_code']) && trim($_POST['year_code']) == '') {		$errorArray['year_code'] = 'Year of the awards required';		$formValid = false;			}		if(count($errorArray) == 0 && $formValid == true) {		$data 	= array();							$data['category_name']	= trim($_POST['category_name']);						$data['year_code']			= trim($_POST['year_code']);					if(isset($categoryData)) {			/*Update. */			$where		= $categoryObject->getAdapter()->quoteInto('category_code = ?', $categoryData['category_code']);			$success	= $categoryObject->update($data, $where);		} else {			$success	= $categoryObject->insert($data);					}				if(count($errorArray) == 0) {			header('Location: /resources/category/');				exit;				}	}		/* if we are here there are errors. */	$smarty->assign('errorArray', $errorArray);	}$smarty->display('resources/category/details.tpl');?>