<?php/* Add this on all pages on top. */set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/*** Standard includes */require_once 'config/database.php';require_once 'config/smarty.php';/*** Check for login */ require_once 'includes/auth.php';/* objects. */require_once 'class/year.php';$yearObject 	= new class_year();if (isset($_GET['code']) && trim($_GET['code']) != '') {		$code = trim($_GET['code']);		$yearData = $yearObject->getByCode($code);	if($yearData) {		$smarty->assign('yearData', $yearData);	} else {		header('Location: /resources/year/');		exit;			}}/* Check posted data. */if(count($_POST) > 0) {	$errorArray		= array();	$data 				= array();	$formValid		= true;	$success			= NULL;		if(isset($_POST['year_name']) && trim($_POST['year_name']) == '') {		$errorArray['year_name'] = 'required';		$formValid = false;			}		if(!isset($yearData)) {		if(isset($_POST['year_code']) && trim($_POST['year_code']) == '') {			$errorArray['year_code'] = 'Year required';			$formValid = false;		} else if(strlen((int)trim($_POST['year_code'])) != 4) {			$errorArray['year_code'] = 'Valid year required';			$formValid = false;		}	}		if(count($errorArray) == 0 && $formValid == true) {		$data 	= array();							$data['year_name']	= trim($_POST['year_name']);								if(isset($yearData)) {			/*Update. */			$where		= $yearObject->getAdapter()->quoteInto('year_code = ?', $yearData['year_code']);			$success	= $yearObject->update($data, $where);		} else {			$data['year_code']		= trim($_POST['year_code']);				$success	= $yearObject->insert($data);					}				if(count($errorArray) == 0) {			header('Location: /resources/year/');				exit;				}	}		/* if we are here there are errors. */	$smarty->assign('errorArray', $errorArray);	}$smarty->display('resources/year/details.tpl');?>